// SPDX-License-Identifier: MIT
pragma solidity ^0.8.2;

import "./Ownable.sol";
import "./SafeMath.sol";
import "./IBEP20.sol";
import "./IRobotCore.sol";
import "./IPieceCore.sol";

contract MekaDeployer is Ownable {
       using SafeMath for uint256;

    address _mekaAddress;
    address _adminAddress;
    address _robotCoreAddress;
    address _pieceCoreAddress;
    uint256 _minConversion;
    mapping(uint256 => bool) usedNonces;
    mapping(uint256 => bool) usedAttachNonces;
    event BuyMeka(address _receiver, uint256 amount);
    event BuyOres(address _receiver, uint256 amount);

    constructor (address meka, address robotCore, address pieceCore){
        _mekaAddress = meka;
        _adminAddress = owner();
        _robotCoreAddress = robotCore;
        _pieceCoreAddress = pieceCore;
        _minConversion = 10 * 10 ** 18;
    }

    function setMekaMinersAddress(address meka) external onlyOwner {
        _mekaAddress = meka;
    }

    function setAdminAddress(address admin) external onlyOwner {
        _adminAddress = admin;
    }

    function setMekaConversionRate(uint256 amount) external onlyOwner {
        _minConversion = amount * 10 ** 18;
    }

    function convertToMeka(address _receiver, uint256 _amount, uint _nonce, bytes memory _signature) external {
        require(_receiver != address(0)); 
        require(verify(_receiver, _amount, _nonce, _signature), "not signed");
        require(!usedNonces[_nonce]);
        require(IBEP20(_mekaAddress).transfer(_receiver, _amount));
        usedNonces[_nonce] = true;
        
        emit BuyMeka(_receiver, _amount);
    }
     
    function convertFromMeka(uint256 _amount, uint _nonce, bytes memory _signature) external {
        require(msg.sender != address(0)); 
        require(_amount >= _minConversion, "lower than necessary"); 
        require(verify(msg.sender, _amount, _nonce, _signature), "not signed");
        require(!usedNonces[_nonce]);
        require(IBEP20(_mekaAddress).transferFrom(msg.sender, address(this), _amount), "insuficient balance");
        usedNonces[_nonce] = true;
        
        emit BuyOres(msg.sender, _amount);
    }    
    
    function attachToRobot(address _owner, uint256 _robotId, uint256 _pieceId, uint256 _amount, uint _nonce ,bytes memory _signature) external {
        require(msg.sender != address(0)); 
        require(_robotId != 0, "invalid robotId");
        require(verify(_owner, _amount, _nonce, _signature), "not signed");
        require(!usedAttachNonces[_nonce]);
        
        usedAttachNonces[_nonce] = true;
        IPieceCore(_pieceCoreAddress).burnPiece(_pieceId);
        IRobotCore(_robotCoreAddress).attachPiece(_owner, _robotId, _pieceId, _amount);
    }

    function createRobot(uint256 _amount, uint _nonce, string memory _tokenURI ,bytes memory _signature) external {
        require(msg.sender != address(0)); 
        require(verify(msg.sender, _amount, _nonce, _signature), "not signed");
        require(!usedNonces[_nonce]);
        
        usedNonces[_nonce] = true;
        IRobotCore(_robotCoreAddress).createRobot(msg.sender, _amount, _tokenURI);
    }

    function createPiece(uint256 _amount, uint256 _appearenceId, uint _nonce, string memory _tokenURI ,bytes memory _signature) external {
        require(msg.sender != address(0)); 
        require(verify(msg.sender, _amount, _nonce, _signature), "not signed");
        require(!usedNonces[_nonce]);
        
        usedNonces[_nonce] = true;
        IPieceCore(_pieceCoreAddress).createPiece(msg.sender, _amount, _appearenceId, _tokenURI);
    }

    function getMessageHash(
        address _to,
        uint _amount,
        uint _nonce
    ) public pure returns (bytes32) {
        return keccak256(abi.encodePacked(_to, _amount, _nonce));
    }

    function getEthSignedMessageHash(bytes32 _messageHash)
        public
        pure
        returns (bytes32)
    {
        return
            keccak256(
                abi.encodePacked("\x19Ethereum Signed Message:\n32", _messageHash)
            );
    }

    function verify(
        address _to,
        uint _amount,
        uint _nonce,
        bytes memory _signature
    ) public view returns (bool) {
        bytes32 messageHash = getMessageHash(_to, _amount, _nonce);
        bytes32 ethSignedMessageHash = getEthSignedMessageHash(messageHash);

        return recoverSigner(ethSignedMessageHash, _signature) == _adminAddress;
    }

    function recoverSigner(bytes32 _ethSignedMessageHash, bytes memory _signature)
        public
        pure
        returns (address)
    {
        (bytes32 r, bytes32 s, uint8 v) = splitSignature(_signature);

        return ecrecover(_ethSignedMessageHash, v, r, s);
    }

    function splitSignature(bytes memory sig)
        public
        pure
        returns (
            bytes32 r,
            bytes32 s,
            uint8 v
        )
    {
        require(sig.length == 65, "invalid signature length");

        assembly {
            r := mload(add(sig, 32))
            s := mload(add(sig, 64))
            v := byte(0, mload(add(sig, 96)))
        }
    }
}
